    18	// Initialize extension
    19	chrome.runtime.onInstalled.addListener(async () => {
    20	  console.log('Group Order Extra Calculator installed');
    21	  
    22	  // Set default settings if not already set
    23	  const settings = await chrome.storage.sync.get(DEFAULT_SETTINGS);
    24	  await chrome.storage.sync.set(settings);
    25	});
    26	
    27	// Handle messages from content script and popup
    28	chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
    29	  switch (message.type) {
    30	    case 'GET_SETTINGS':
    31	      handleGetSettings(sendResponse);
    32	      return true; // Keep message channel open for async response
    33	      
    34	    case 'UPDATE_SETTINGS':
    35	      handleUpdateSettings(message.settings, sendResponse);
    36	      return true;
    37	      
    38	    case 'CALCULATE_ORDER':
    39	      handleCalculateOrder(message.data, sendResponse);
    40	      return true;
    41	      
    42	    case 'LOG_ERROR':
    43	      console.error('Content script error:', message.error);
    44	      break;
    45	      
    46	    default:
    47	      console.warn('Unknown message type:', message.type);
    48	  }
    49	});
