   290	  /**
   291	   * Clear sensitive data from memory
   292	   */
   293	  clearSensitiveData(obj) {
   294	    if (typeof obj === 'string') {
   295	      // Overwrite string memory (best effort)
   296	      return obj.replace(/./g, '0');
   297	    } else if (obj instanceof ArrayBuffer || obj instanceof Uint8Array) {
   298	      // Clear buffer
   299	      if (obj instanceof ArrayBuffer) {
   300	        new Uint8Array(obj).fill(0);
   301	      } else {
   302	        obj.fill(0);
   303	      }
   304	    } else if (typeof obj === 'object' && obj !== null) {
   305	      // Clear object properties
   306	      for (const key in obj) {
   307	        if (obj.hasOwnProperty(key)) {
   308	          obj[key] = null;
   309	        }
