   356	  /**
   357	   * Store security logs with automatic cleanup
   358	   */
   359	  async storeSecurityLog(logEntry) {
   360	    try {
   361	      const result = await chrome.storage.local.get(['securityLogs']);
   362	      let logs = result.securityLogs || [];
   363	      
   364	      // Add new log
   365	      logs.push(logEntry);
   366	      
   367	      // Keep only last 100 entries
   368	      if (logs.length > 100) {
   369	        logs = logs.slice(-100);
   370	      }
   371	      
   372	      // Remove logs older than 7 days
   373	      const weekAgo = Date.now() - (7 * 24 * 60 * 60 * 1000);
   374	      logs = logs.filter(log => new Date(log.timestamp).getTime() > weekAgo);
   375	      
   376	      await chrome.storage.local.set({ securityLogs: logs });
   377	    } catch (error) {
   378	      console.error('Failed to store security log:', error);
   379	    }
   380	  }
