  1399	  /**
  1400	   * Send message to background script with timeout and retry logic
  1401	   */
  1402	  function sendMessage(message, timeout = 5000, retries = 2) {
  1403	    return new Promise((resolve, reject) => {
  1404	      let attemptCount = 0;
  1405	
  1406	      function attemptSend() {
  1407	        attemptCount++;
  1408	
  1409	        const timeoutId = setTimeout(() => {
  1410	          reject(new Error(`Message timeout after ${timeout}ms (attempt ${attemptCount}/${retries + 1})`));
  1411	        }, timeout);
  1412	
  1413	        try {
  1414	          chrome.runtime.sendMessage(message, (response) => {
  1415	            clearTimeout(timeoutId);
  1416	
  1417	            if (chrome.runtime.lastError) {
  1418	              const error = new Error(chrome.runtime.lastError.message);
  1419	
  1420	              // Retry on connection errors
  1421	              if (attemptCount <= retries && (
  1422	                error.message.includes('Could not establish connection') ||
  1423	                error.message.includes('Extension context invalidated')
  1424	              )) {
  1425	                console.warn(`Message failed (attempt ${attemptCount}), retrying...`, error.message);
  1426	                setTimeout(attemptSend, 1000 * attemptCount); // Exponential backoff
  1427	                return;
  1428	              }
  1429	
  1430	              reject(error);
  1431	            } else {
  1432	              resolve(response);
  1433	            }
  1434	          });
  1435	        } catch (error) {
  1436	          clearTimeout(timeoutId);
  1437	          reject(error);
  1438	        }
  1439	      }
  1440	
  1441	      attemptSend();
  1442	    });
  1443	  }
