     1	/**
     2	 * Security utilities for protecting sensitive data in the Chrome extension
     3	 * Implements encryption, validation, and secure storage practices
     4	 */
     5	
     6	class SecurityManager {
     7	  constructor() {
     8	    this.keyDerivationIterations = 100000;
     9	    this.algorithm = 'AES-GCM';
    10	    this.keyLength = 256;
    11	    this.ivLength = 12; // 96 bits for GCM
    12	  }
    13	
    14	  /**
    15	   * Generate a cryptographic key from extension ID and user context
    16	   */
    17	  async generateKey() {
    18	    try {
    19	      const extensionId = chrome.runtime.id;
    20	      const userAgent = navigator.userAgent;
    21	      const timestamp = Date.now().toString();
    22	      
    23	      // Create a unique seed from extension context
    24	      const seed = `${extensionId}-${userAgent}-${timestamp}`;
    25	      const encoder = new TextEncoder();
    26	      const seedBuffer = encoder.encode(seed);
    27	      
    28	      // Derive key using PBKDF2
    29	      const baseKey = await crypto.subtle.importKey(
    30	        'raw',
    31	        seedBuffer,
    32	        'PBKDF2',
    33	        false,
    34	        ['deriveKey']
    35	      );
